set-payload.value:
#[%dw 2.0
output application/json
---
{
    return: false,
    direct: true,
    payload: payload
}]

ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
	directFlights: flatten([
	    payload[0].payload,
	    payload[1].payload,
	    payload[2].payload
	])
}

